while (QuestLogManager.GetQuestById(66371).Step == 1)
{
    var explosive = InventoryManager.FilledSlots.FirstOrDefault(x => x.RawItemId == 2000651);
    await CommonTasks.StopAndDismount();
    foreach (var x in GameObjectManager.GetObjectsOfType<EventObject>(true))
    {
        if (x.IsVisible && (x.NpcId == 2002053 || x.NpcId == 2002054 || x.NpcId == 2002055 ))
        {
            while (Core.Player.Location.Distance(x.Location) > 5f)
            {
                ff14bot.Pathing.Flightor.MoveTo(x.Location);
                await Coroutine.Yield();
            }
            await CommonTasks.StopMoving();
            await Coroutine.Sleep(500);
            explosive.UseItem(x.Location);
            await Coroutine.Sleep(4000);
            break;
        }
    }
}


